mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, a.* FROM ( SELECT ad.ano, ad.apasswd, ad.atel, ad.aemail, ad.aname, ad.hiredate, ad.reg_date, cm1.CONTENT AS level_content, -- cm1의 CONTENT 컬럼 cm2.CONTENT AS status_content, -- cm2의 CONTENT 컬럼 cm1.bcd AS cm1_bcd, -- cm1의 bcd 컬럼 cm2.bcd AS cm2_bcd -- cm2의 bcd 컬럼 FROM common cm1 INNER JOIN admin ad ON ad.alevel_bcd = cm1.bcd AND ad.alevel_mcd = cm1.mcd INNER JOIN common cm2 ON ad.astatus_bcd = cm2.bcd AND ad.astatus_mcd = cm2.mcd
where aname LIKE '%' || #{search.search1} || '%'
ORDER BY TO_NUMBER(ano) )a ) WHERE rn BETWEEN #{startRow} AND #{endRow}

update.if:
UPDATE admin set aname = #{aname}
				,atel = #{atel}
				,aemail = #{aemail}
				,alevel_mcd = #{alevelMcd}
				,astatus_mcd =  #{astatusMcd}
WHERE ano = #{ano}
