mapper.select:
SELECT COUNT(*) FROM APPOINTMENT a JOIN MEMBER m ON a.MEMNO = m.MEMNO JOIN PET p ON a.PETNO = p.PETNO JOIN ADMIN d ON a.ANO = d.ANO JOIN MEDICAL_ITEM t ON a.MCODE = t.MCODE JOIN COMMON c ON c.BCD = a.RSTATUS_BCD and c.MCD = a.RSTATUS_MCD JOIN COMMON cm ON cm.BCD = t.MTITLE_BCD and cm.MCD = t.MTITLE_MCD
WHERE 1=1
AND a.petno = #{search4}
AND a.rstatus_mcd = #{type4}
AND a.RESNO LIKE '%' || #{search1} || '%' AND m.MNAME LIKE '%' || #{search1} || '%' AND p.PETNAME LIKE '%' || #{search1} || '%' AND d.ANAME LIKE '%' || #{search1} || '%' AND cm.CONTENT LIKE '%' || #{search1} || '%'
AND a.rdate BETWEEN TO_DATE(#{start_date}, 'yyyy-mm-dd') AND TO_DATE(#{end_date}, 'yyyy-mm-dd')

SELECT * FROM ( SELECT ROWNUM rn, aa.* FROM ( SELECT a.RESNO, a.RDATE, m.MNAME, p.PETNAME, d.ANAME, c2.CONTENT as item, c1.CONTENT as status, a.rstatus_mcd , c3.CONTENT as species, c4.CONTENT as gender, a.start_time FROM APPOINTMENT a JOIN MEMBER m ON a.MEMNO = m.MEMNO JOIN PET p ON a.PETNO = p.PETNO JOIN ADMIN d ON a.ANO = d.ANO JOIN MEDICAL_ITEM t ON a.MCODE = t.MCODE JOIN COMMON c1 ON c1.BCD = a.RSTATUS_BCD and c1.MCD = a.RSTATUS_MCD JOIN COMMON c2 ON c2.BCD = t.MTITLE_BCD and c2.MCD = t.MTITLE_MCD JOIN COMMON c3 ON c3.BCD = p.PETSPECIES_BCD and c3.MCD = p.PETSPECIES_MCD JOIN COMMON c4 ON c4.BCD = p.PETGENDER_BCD and c4.MCD = p.PETGENDER_MCD
WHERE 1=1
AND a.petno = #{si.search4}
AND a.rstatus_mcd = #{si.type4}
AND a.RESNO LIKE '%' || #{si.search1} || '%' AND m.MNAME LIKE '%' || #{si.search1} || '%' AND p.PETNAME LIKE '%' || #{si.search1} || '%' AND d.ANAME LIKE '%' || #{si.search1} || '%' AND c2.CONTENT LIKE '%' || #{si.search1} || '%'
AND a.rdate BETWEEN TO_DATE(#{si.start_date}, 'yyyy-mm-dd') AND TO_DATE(#{si.end_date}, 'yyyy-mm-dd')
ORDER BY a.REG_DATE desc )aa ) WHERE rn BETWEEN #{startRow} AND #{endRow}

SELECT PETNO, PETNAME, PETBIRTH
FROM PET
WHERE MEMNO = #{memno}
