mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, bb.* FROM ( SELECT b.bno, b.btitle, m.mid, b.reg_date, b.bview_count, b.board_type_mcd, c.content FROM BOARD b JOIN member m ON b.memno = m.memno JOIN (SELECT mcd,content FROM common WHERE bcd= 350) c ON b.board_type_mcd = c.mcd WHERE b.btitle IS NOT NULL
AND
board_type_mcd = #{type4}
AND
btitle LIKE '%' || #{search1} || '%'
ORDER BY b.bno desc )bb ) WHERE rn BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE BOARD
SET bstatus_mcd = 200
WHERE bno = #{bno}

mapper.select:
SELECT *
FROM board
WHERE orderno = #{orderno} AND pno = #{pno}

mapper.update:
UPDATE board SET bcontents = #{board.bcontents},
bimg = #{board.bimg},
update_date = sysdate WHERE orderno = #{board.orderno} AND pno = #{board.pno}
