mapper.select:
SELECT SUM(pprice*oquantity)
FROM orderproduct
WHERE orderno = #{orderno}

SELECT pc.*, cm1.content opayment_content, cm2.content ostatus_content FROM purchase pc JOIN common cm1 ON pc.opayment_bcd = cm1.bcd AND pc.opayment_mcd = cm1.mcd JOIN common cm2 ON pc.ostatus_bcd = cm2.bcd AND pc.ostatus_mcd = cm2.mcd WHERE pc.memno = #{memno}
ORDER BY pc.odate
ORDER BY pc.odate desc

SELECT memno
FROM purchase
WHERE orderno = #{orderno}

SELECT pname
FROM product
WHERE pno = #{pno}

mapper.update:
{ CALL ADD_PURCHASE(
    #{memno,       mode=IN,    jdbcType=NUMERIC},
    #{opayment_mcd, mode=IN,    jdbcType=NUMERIC},
    #{ostatus_mcd,    mode=IN,    jdbcType=NUMERIC},
    #{ototal_price, mode=IN,    jdbcType=NUMERIC},
    #{orderno,       mode=OUT,    jdbcType=NUMERIC}
) }

 UPDATE PURCHASE
SET ostatus_mcd = 100 , tid = #{tid}
WHERE orderno = #{orderno}
