mapper.update:
UPDATE PET SET MEMNO = #{pet.memno}, PETNAME = #{pet.petname}, PETBIRTH = #{pet.petbirth}, PETSPECIAL = #{pet.petspecial},
PETIMG = #{pet.petimg},
PETSPECIES_BCD = #{pet.petspecies_bcd}, PETSPECIES_MCD = #{pet.petspecies_mcd}, PETGENDER_MCD = #{pet.petgender_mcd} WHERE PETNO = #{pet.petno}

UPDATE PET SET
	PETWEIGHT = #{petweight}
WHERE PETNO = #{petno}

mapper.select:
SELECT COUNT(*)
FROM WEIGHT
WHERE PETNO = #{petno}

SELECT *
FROM (
    SELECT ROWNUM rn, ww.*
    FROM (
        SELECT PETNO, MEMNO, REG_DATE, PETWEIGHT
		FROM WEIGHT
	WHERE PETNO = #{petno}
		ORDER BY reg_date DESC
    )ww
)
WHERE rn BETWEEN #{startRow} AND #{endRow}
