mapper.select:
SELECT COUNT(*) FROM BOARD WHERE BSTATUS_MCD = 100 AND BSEQ = 0
AND BOARD_TYPE_MCD = #{type4}
AND BOARD_TYPE_MCD != 300
AND MEMNO = #{type5}
AND BTITLE LIKE '%' || #{search1} || '%'

SELECT * FROM ( SELECT ROWNUM rn, bb.* FROM ( SELECT b.BNO, b.BTITLE, b.BCONTENTS, b.REG_DATE, b.RESNO, b.BOARD_TYPE_MCD, b.MEMNO, mask_email(m.MEMAIL) memail FROM BOARD b JOIN MEMBER m ON b.MEMNO = m.MEMNO WHERE b.BSTATUS_MCD = 100 AND BSEQ = 0
AND b.BOARD_TYPE_MCD = #{si.type4}
AND b.BOARD_TYPE_MCD != 300
AND b.MEMNO = #{si.type5}
AND b.BTITLE LIKE '%' || #{si.search1} || '%'
ORDER BY b.BNO DESC )bb ) WHERE rn BETWEEN #{startRow} AND #{endRow}

		{
			CALL insert_board_with_image(
	            #{btitle,		mode=IN,	jdbcType=VARCHAR}, 
	            #{bcontents, 	mode=IN,	jdbcType=VARCHAR}, 
	            #{resno, 		mode=IN,	jdbcType=VARCHAR}, 
	            #{memno, 		mode=IN,	jdbcType=INTEGER},
	            #{bno, 			mode=OUT,	jdbcType=INTEGER}
	        )
        }

SELECT COUNT(*) 
FROM BOARD 
WHERE BSTATUS_MCD = 100 
	AND MEMNO = #{type5}

	AND BOARD_TYPE_MCD = #{type4} 


	AND (BTITLE LIKE '%' || #{search1} || '%' OR dbms_lob.instr(BCONTENTS, #{search1}) > 0)

SELECT * FROM ( SELECT ROWNUM rn, bb.* FROM ( SELECT b.BNO, b.BTITLE, b.BCONTENTS, b.REG_DATE, b.RESNO, b.BOARD_TYPE_MCD, b.MEMNO, b.BGROUP, mask_email(m.MEMAIL) memail FROM BOARD b JOIN MEMBER m ON b.MEMNO = m.MEMNO WHERE b.BSTATUS_MCD = 100 AND b.MEMNO = #{si.type5}
AND b.BOARD_TYPE_MCD = #{si.type4}
AND (b.BTITLE LIKE '%' || #{si.search1} || '%' OR dbms_lob.instr(b.BCONTENTS, #{si.search1}) > 0)
ORDER BY b.BNO DESC )bb ) WHERE rn BETWEEN #{startRow} AND #{endRow}

mapper.update:

UPDATE BOARD 
SET BSTATUS_MCD = 200 
WHERE BNO = #{bno}
